def parse_input(text):
    lines = text.strip().split('\n')
    matrices = [list(map(int, line.split())) for line in lines]
    return matrices


def get_dict(array):
    dict = {}
    for item in array:
        for i in range(item[2]):
            dict[item[1] + i] = item[0] + i
    return dict


def translate(array_input, dictionary):
    array_output = []
    for item in array_input:
        try:
            array_output.append(dictionary[item])
        except:
            array_output.append(item)
    return array_output


def translate_nodict(array_input, input_matrix):
    array_output = []
    for index,seed in enumerate(array_input):
        for item in input_matrix:
            if seed >= item[1] and seed < item[1] + item[2]:
                array_output.append(item[0] + (seed-item[1]))
                break
        if (index != len(array_output)-1):
            array_output.append(seed)
    return array_output


